DECLARE @UpdSSTPDMult nvarchar(max) = ''
SET @UpdSSTPDMult = 'UPDATE T SET
					SS_TPD_MULT = coalesce(SMOKE.FACTOR, DEF_SMOKE.FACTOR) * coalesce(OCC.FACTOR, DEF_OCC.FACTOR)
				FROM ' + @InforceTableName + ' As T
