		and risk.row_start_date = ( SELECT min(risk2.row_start_date)
		                        FROM  Temp_Contract_Risk_WMS risk2
								WHERE risk.edw_contract_risk_key = risk2.edw_contract_risk_key
								 AND risk.EDW_Contract_Key = risk2.EDW_Contract_Key
