		--
		UNION ALL
		--
		select  risk.[EDW_Contract_Key],
		    risk.[EDW_Product_Key],
			risk.[EDW_Product_Option_Key], 
			risk.edw_product_option_code,
			src.Incurred_Date,
			--
			risk.edw_contract_risk_key,
			risk.[EDW_Risk_Status], 
			risk.[EDW_Risk_Claim_Ind], 
