	FROM [dbo].[Reins_Claim_Listing] trg
	INNER JOIN Able_Claim_Listing A
	ON 	'AB' + cast(A.Claim_Benefit_Id as varchar) = trg.Claim_Source_Id
	and A.EDW_Contract_Key = trg.EDW_Contract_Key
