			, i.QS_AMT_REINP_BCKD
			, i.QS_AMT_IC_BASE
			, i.QS_AMT_IC_INCR
			, i.QS_AMT_OC_CURR
			, i.QS_AMT_OC_BCKD
			, i.QS_AMT_OCX_CURR
			, i.QS_AMT_OCX_BCKD
			, i.QS_AMT_LAPSE_RVS
			, i.SS_ANN_PREM
