				--WHEN LOWER(Data_Type) like 'date%' THEN ', CASE WHEN ['+column_name+'] <= CONVERT('+Data_Type+',''01/jan/1753'') THEN NULL ELSE ['+column_name+'] END ['+column_name+']'
