					)

					set @UpdateRowCount = @UpdateRowCount + @@Rowcount

					-- Carry these records forward.
					INSERT INTO [dbo].[Reins_Claim_Expense_Trnx] (
					Source_Trnx_Id,
					[Claim_Expense_Id],
					[Transaction_Type],
					[Assumed_Paid],
					[Assumed_Paid_Date],
					[PRODUCT_SYSTEM_CODE],
					[Policy_number],
					[Month_Effective_date],
