					[Reins_Benefit_Category],
					[Created_Date_Time],
					Created_By,
					Process_Log_ID

						
					)
					SELECT 
					[dbo].[Reins_Mercury_Trnx].Mercury_Trnx_Id,
					@NEWClaimExpId,
					[dbo].[Reins_Mercury_Trnx].Transaction_Type, 
					'Y',
					[dbo].[Reins_Mercury_Trnx].Transaction_Process_Date as Assumed_Paid_Date, 
