	--Progress to recovery rule for IP
		set @sqlstatement = '
							Update Reins_Claim_Expense_Trnx
							SET 
								Progress_to_Recovery = ''Y''
								,Progress_to_Recovery_rule = ''Paid_Claim_Treaty_IP''
							from 
								Reins_Claim_Expense_Trnx A 
