			)  '

	--PRINT @sqlstatement;
	Begin Transaction
	exec(@sqlstatement);
	COMMIT Transaction

	--PRINT 'Policy List sql executed'
	--Update #Get_Policy_list
	--SET Contract_Key = B.Contract_key
	--FROM #Get_Policy_list A, [STG].[Stg_EDW_Contract] B
	--where A.Policy_number = B.DISPLAY_CONTRACT_ID
	

